package a.array;

public class DemoDu4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr = {2 , 4, 5 ,8};
		
		for (int i = 0; i < arr.length; i++) {
			System.out.println(arr[i]);
			
		}
		System.out.println("======");
		
		for(int x: arr) {
			System.out.println(x);
		}

	}

}
